import osimport wfdbimport pandas as pdfile_Amount = 75file_Path = '/Users/collcertaye/WorkSpace/Lab-FECG/FECG_2013DB/set-a'save_Path = '/Users/collcertaye/WorkSpace/Lab-FECG/FECG_2013DB/dataset/'# get file name a1-a75def name_list():    file_name = []    file_list = range(file_Amount)    for loop_i in file_list:        if loop_i < 9:            file_read = 'a0' + str(loop_i + 1)            pass        else:            file_read = 'a' + str(loop_i + 1)        print("file_name: " + file_read)        file_name.append(file_read)        pass    return file_name# make dataset and save as .csv filedef dataset_make():    file_name = name_list()    for file_load in file_name:        record = wfdb.rdrecord(os.path.join(file_Path, file_load), sampfrom=0, physical=True, channels=[0, 1, 2, 3])        signal_record = record.p_signal[:, :]        # print(len(signal_record))        signal_record = pd.DataFrame({'lead0': signal_record[:, 0], 'lead1': signal_record[:, 1],                                      'lead2': signal_record[:, 2], 'lead3': signal_record[:, 3]})        signal_record.to_csv(save_Path + file_load + '_lead.csv', index=False)        # save annotations        signal_annotation = wfdb.rdann(os.path.join(file_Path, file_load), 'atr')        annotation_id = signal_annotation.sample        annotation_type = signal_annotation.symbol        # print(len(annotation_id))        annotation_frame = pd.DataFrame({'ann_id': annotation_id, 'ann_type': annotation_type})        annotation_frame.to_csv(save_Path + file_load + '_ann.csv', index=False)        pass    print("dataset make complete !")dataset_make()