import osimport wfdbimport pandas as pdimport matplotlib.pyplot as pltfileName = ['100', '101', '106', '108', '109', '112', '114', '115', '116', '118', '119', '122',            '124', '201', '203', '205', '207', '208', '209', '215', '220', '223', '230', '234',            '103', '105', '111', '113', '117', '121', '123', '200', '202', '210', '212', '213',            '214', '219', '221', '222', '228', '231', '232', '233']filePath = '/Users/collcertaye/WorkSpace/Laboratory_FECG/MIT:BIH_database/mit-bih-arrhythmia-database-1.0.0'trainerPath = '/Users/collcertaye/WorkSpace/Laboratory_FECG/FECG_Detection/MIT-BIH-ECG/'def read_trainset(file_path, file_name):    for file_load in file_name:        record = wfdb.rdrecord(os.path.join(file_path, file_load), physical=True, channels=[0, 1])        signal_record0 = record.p_signal[:, 0]        signal_record1 = record.p_signal[:, 1]        signal_annotation = wfdb.rdann(os.path.join(file_path, file_load), 'atr')        annotation_id = signal_annotation.sample        annotation_type = signal_annotation.symbol        trainingSet[file_load] = {'lead0': signal_record0, 'lead1': signal_record1,                                  'annIdx': annotation_id, 'annType': annotation_type}trainingSet = {}read_trainset(filePath, fileName)# save the data as .csv formatheaders = ['lead', 'annIdx', 'annType']for k, v in trainingSet.items():    signal_frame = pd.DataFrame({'lead0': trainingSet[k]['lead0'], 'lead1': trainingSet[k]['lead1']})    signal_frame.to_csv(trainerPath + k + '_lead.csv', index=False)    annotation_frame = pd.DataFrame({'annIdx': trainingSet[k]['annIdx'], 'annType': trainingSet[k]['annType']})    annotation_frame.to_csv(trainerPath + k + '_ann.csv', index=False)    print(k, end='')# test the datasetplt.figure(figsize=(20, 6), dpi=80)temp1 = trainingSet['109']['lead0'][:6000]plt.plot(temp1)plt.show()