import pywt# denoise siganl with waveletdef wavelet_denoise(signal_lead):    signal_lead = list(signal_lead)    index = []    data = []    for i in range(len(signal_lead)-1):        loop_i = float(i)        value = float(signal_lead[i])        index.append(loop_i)        data.append(value)        pass    pass    # Create wavelet object and define parameters    w = pywt.Wavelet('db8')    maxlev = pywt.dwt_max_level(len(data), w.dec_len)    # print("maximum level is " + str(maxlev))    threshold = 0.04  # Threshold for filtering    # Decompose into wavelet components, to the level selected:    coeffs = pywt.wavedec(data, 'db8', level=maxlev)    for i in range(1, len(coeffs)):        coeffs[i] = pywt.threshold(coeffs[i], threshold*max(coeffs[i]))        pass    pass    datarec = pywt.waverec(coeffs, 'db8')    return datarec